import React from "react";
import axios from "axios";

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = { name: "", topic: "współpraca", message: "" };

    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleNameChange = e => {
    this.setState({ name: e.target.value });
  };
  handleEmailChange = e => {
    this.setState({ email: e.target.value });
  };
  handleTopicChange = e => {
    this.setState({ topic: e.target.value });
  };
  handleMessageChange = e => {
    this.setState({ message: e.target.value });
  };

  handleSubmit(event) {
    event.preventDefault();

    alert(
      "Dzięki " +
        this.state.name +
        " za wiadomość! Odpowiemy najszybciej jak to tylko mozliwe!"
    );
    const { name, email, topic, message } = this.state;
    axios.post("/", { name, email, topic, message }).then(result => {});
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Imię
          <input
            type="text"
            value={this.state.name}
            onChange={this.handleNameChange}
          />
        </label>
        <label>
          Email
          <input
            type="email"
            value={this.state.email}
            pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$"
            onChange={this.handleEmailChange}
          />
        </label>
        <label>
          Wybierz temat wiadomości
          <select value={this.state.topic} onChange={this.handleTopicChange}>
            <option value="dostępność terminów">Dostępność terminów</option>
            <option value="wycena">Wycena</option>
            <option value="współpraca">Współpraca</option>
            <option value="inne">Inne</option>
          </select>
        </label>
        <label>
          Tu wpisz treść
          <textarea
            value={this.state.message}
            onChange={this.handleMessageChange}
          />
        </label>
        <input type="submit" value="Wyślij" />
      </form>
    );
  }
}

export default Form;
