import React from "react";
import styled from "styled-components";
import introImg from "./Task1/assets/IntroBackground.jpg";

import * as Section from "components/PageSection";

export const Intro = () => (
  <Section.Wrapper>
    <Section.Content>
      <Section.Header>
        <Section.Title>Witaj w Halina Band</Section.Title>
        <Section.Description>
          Halina Band to zespół który powstał z przyjaźni i dla przyjaźni.
        </Section.Description>
      </Section.Header>
    </Section.Content>
  </Section.Wrapper>
);

export const Wrapper = styled.div`
  position: relative;
  top: 5rem;
  background: #fbe0ce;
`;

export const Content = styled.div`
  background-image: ${introImg};
`;
